import styled from "styled-components";

export const Formulario = styled.div `
flex: 1;
margin-top: 20px;
text-align:center;


form{
    width: 50%;
    text-align:center;
}

button{
    margin-top: 10px;
    background-color: #CCC;
    color:#fff;
    border:0px;
    width: 100%;
    font-size: 15px;
    font-weight:bold;
    height: 30px;
    cursor: pointer;
}
`
