const ACTIONS = {
    ADD: "LIST_ADD",
}

const INITIAL_STATE = {
    lista: []
}


export const addReducer = (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case ACTIONS.ADD:
            const items = [...state.lista, action.item]
            return { ...state, lista: items }
        default:
            return state
    }
}

export const save = (item) => {
    return {
        type: ACTIONS.ADD,
        item
    }

}
