import axios from 'axios'


const http = axios.create({
    baseURL: 'https://api.punkapi.com/v2/'
})

const ACTIONS = {
    LIST: "LIST_DATA",
    ADD: "LIST_ADD",
}

const INITIAL_STATE = {
    list:[]
}

export const listReducer = (state=INITIAL_STATE, action) => {
    switch (action.type) {
        case ACTIONS.LIST:
            console.log("state", state)
            return { ...state, list:action.list }
        case ACTIONS.ADD:
            const lista =  [...state.tarefas, action.tarefa]
            return { ...state,
                list:lista,
                }
        default:
            return state
    }
}

export function save(item) {
    return dispatch => {
        http.post('/beers', item)
        .then(response=>{
            console.log('response', response)
            dispatch([{
                type: ACTIONS.ADD,
                item: response.data
            }])
        })
    }

}


export function list(){
    return dispatch =>{
        http.get('/beers')
        .then(response=>{
            console.log('response', response.data)
            dispatch({
                type: ACTIONS.LIST,
                list: response.data
            })
        })
    }
}
