import axios from 'axios'


const http = axios.create({
    baseURL: 'https://api.punkapi.com/v2/'
})

const ACTIONS = {
    LIST: "LIST_DATA",
    ADD: "LIST_ADD",
}

const INITIAL_STATE = {
    list: list
}

export const listReducer = (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case ACTIONS.LIST:
            return { ...state, list: action.list }
        case ACTIONS.ADD:
            const items = [...state.list, action.item]
            return {...state, list :items}
        default:
            return state
    }
}

export const save = (item) => {
    return {
        type: ACTIONS.ADD,
        item
    }

}

export function list() {
    return dispatch => {
        http.get('/beers')
            .then(response => {
                dispatch({
                    type: ACTIONS.LIST,
                    list: response.data
                })
            })
    }
}
